function addBrandLogo() {
  brendsLogoImage.forEach(function (e) {
    var i = document.querySelector(".brands_logo"),
        s = document.createElement("img");
    s.className = "brends_logo_item", s.alt = e.name, s.src = e.url, i.append(s);
  });
}

function addStatistic() {
  statisticItem.forEach(function (e) {
    var i = document.querySelector(".statistics_wraper"),
        s = document.createElement("div");
    s.className = "statistics_item", s.innerHTML = "<span class=\"item_data\">".concat(e.data, "</span>\n                        <p class=\"item_subscription\">").concat(e.subscription, "</p>"), i.append(s);
  });
}

function addCards(e, i) {
  i.forEach(function (i) {
    var s = document.querySelector(e),
        r = document.createElement("div");
    r.className = "card_item", r.innerHTML = "<img src='".concat(i.url, "' alt='").concat(i.name, "'>\n                                <h3>").concat(i.name, "</h3>\n                                <p>").concat(i.text, "</p>"), s.append(r);
  });
}

function addSlide(e, i) {
  var s = document.querySelector(e);
  i.forEach(function (e) {
    var i = document.createElement("div");
    i.className = "slide_item", i.innerHTML = "<img src='".concat(e.url, "' alt='").concat(e.imgAlt, "' class=\"slide_img\">\n                        <img src='").concat(e.logo, "' alt='").concat(e.logoAlt, "'  class=\"slide_logo\">\n                                <p>").concat(e.text, "</p>"), s.append(i);
  });
  var r = 0;
  document.querySelector(".prev_slide").addEventListener("click", function () {
    r += 300, r > 1750 && (r = -1600), s.style.left = r + "px";
  }), document.querySelector(".next_slide").addEventListener("click", function () {
    r -= 300, r < -1750 && (r = 1600), s.style.left = r + "px";
  });
}

function addIndustryCards() {
  industryData.forEach(function (e) {
    var i = document.querySelector(".industry_cards"),
        s = document.createElement("div");
    s.className = "industry_card_item", s.innerHTML = "<img src='".concat(e.logo, "' alt='").concat(e.logoAlt, "'>\n                                <h3>").concat(e.title, "</h3>\n                                <p>").concat(e.text, "</p>\n                                <a href='").concat(e.linkUrl, "'>").concat(e.linkText, "</a>"), i.append(s);
  });
}

